    def challenge_1
      input.count("(") - input.count(")")
    end

    def challenge_2
      day_1_array = input.split("")
      count = 0
      answer = 0

      day_1_array.each_with_index do |instruction, idx|
        if instruction == "("
          count = count + 1
        else
          count = count - 1
        end

        if count == -1
          answer = idx + 1
          break
        end
      end

      answer
    end
