    end

    def challenge_1
      direct_santas([Santa.new])
    end

    def challenge_2
      direct_santas([Santa.new, Santa.new])
    end

    def direct_santas(santas)
      houses = { "0-0" => santas.size }
      input.split("").each_with_index do |direction, index|
        santa = santas[index % santas.length]
        santa.process_direction(direction)
        key = "#{santa.x}-#{santa.y}"
        houses[key] = (houses[key] || 0) + 1
      end
      houses.count
    end
  end
end

class Santa
  attr_accessor :x, :y

  def initialize
    self.x = 0
    self.y = 0
  end

  def process_direction(direction)
    self.y = self.y + 1 if direction == "^"
    self.y = self.y - 1 if direction == "v"
    self.x = self.x + 1 if direction == ">"
    self.x = self.x - 1 if direction == "<"
  end
end
